"use strict";

var Entity = require('./entity');

/**
* Inputs entity
*
* @class Inputs
* @constructor
* @param {string} root path
* @param {Express} express application
*/
var Inputs = function(_root, _app) {
	Entity.call(this, _root, _app);
};

Inputs.prototype = Object.create(Entity.prototype);
Inputs.prototype.constructor = Inputs;

/**
* Inputs key
*
* @property KEY
* @type string
* @default "inputs"
*/
Inputs.KEY = "inputs";

/**
* Get the entity's path
*
* @method getPath
*/
Inputs.prototype.getPath = function() {
	return this.root + '/' + Inputs.KEY;
};

module.exports = Inputs;