"use strict";

var Entity = require('./entity');

/**
* Layers entity
*
* @class Layers
* @constructor
* @param {string} root path
* @param {Express} express application
*/
var Layers = function(_root, _app) {
	Entity.call(this, _root, _app);
};

Layers.prototype = Object.create(Entity.prototype);
Layers.prototype.constructor = Layers;

/**
* Layers key
*
* @property KEY
* @type string
* @default "layers"
*/
Layers.KEY = "layers";

/**
* Get the entity's path
*
* @method getPath
*/
Layers.prototype.getPath = function() {
	return this.root + '/' + Layers.KEY;
};

module.exports = Layers;