"use strict";

var Entity = require('./entity');

/**
* Level entity
*
* @class Level
* @constructor
* @param {string} root path
* @param {Express} express application
*/
var Level = function(_root, _app) {
	Entity.call(this, _root, _app);
};

Level.prototype = Object.create(Entity.prototype);
Level.prototype.constructor = Level;

/**
* Level key
*
* @property KEY
* @type string
* @default "prefab"
*/
Level.KEY = "level";

/**
* Get the entity's path
*
* @method getPath
*/
Level.prototype.getPath = function() {
	return this.root + '/' + Level.KEY;
};

module.exports = Level;