"use strict";

var Entity = require('./entity');

/**
* Project entity
*
* @class Project
* @constructor
* @param {string} root path
* @param {Express} express application
*/
var Project = function(_root, _app) {
	Entity.call(this, _root, _app);
};

Project.prototype = Object.create(Entity.prototype);
Project.prototype.constructor = Project;

/**
* Project key
*
* @property KEY
* @type string
* @default "project"
*/
Project.KEY = "project";

/**
* Get the entity's path
*
* @method getPath
*/
Project.prototype.getPath = function() {
	return this.root + '/' + Project.KEY;
};

module.exports = Project;